  <div>
    <svg id="rocket-smoke" xmlns="http://www.w3.org/2000/svg" width="516.119" height="1083.632" viewBox="0 0 516.119 1083.632">
      <title>Rocket Ship Smoke</title>
      <path id="Path_40" data-name="Path 40" d="M644.6,141S143.02,215.537,147.049,870.207s342.774,201.755,342.774,201.755S404.659,847.213,388.815,762.2c-27.116-145.51-11.551-384.124,271.9-609.1C671.15,139.365,644.6,141,644.6,141Z" transform="translate(-147.025 -140.939)" fill="#f5f5f5"/>
    </svg>

  </div>

  <div class="container d-flex flex-column min-vh-50 justify-content-center align-items-center">
    <h1>Note Taking App</h1>

    <button class="btn btn-lg btn-dark mt-3" (click)="onClickBtnCreateNewPost()">
      Create New Note
    </button>

    <div class="mh-500px w-300 overflow-auto d-inline-block mt-5 shadow">
      <table class="table table-striped">
        <thead>
          <tr>
            <th scope="col">ID</th>
            <th scope="col">Title</th>
            <th scope="col">Body</th>
            <th scope="col">Created On</th>
            <th scope="col">Actions</th>
          </tr>
        </thead>
        <tbody>
        <tr *ngFor="let note of noteService.allNotes; index as i">
          <th scope="row">{{ note.id }}</th>
          <td>{{ note.title }}</td>
          <td>{{ note.body }}</td>
          <td>{{ note.createdDate }}</td>
          <td>
            <button class="btn btn-lg btn-dark me-3" (click)="onClickBtnUpdatePost(note)">
              Update
            </button>
            <button class="btn btn-lg btn-outline-dark" (click)="onClickBtnDeletePost(note)">
              Delete
            </button>
          </td>
        </tr>
        </tbody>
      </table>
    </div>
  </div>

